import{_ as s,c as a,o as n,a as e}from"./app.c9b5fec3.js";const l="/assets/zen.ca664383.png",p="/assets/lts.5e2a198d.png",o="/assets/hardened.f4524fc1.png",t="/assets/kernel-grub-0.0be26c74.png",r="/assets/kernel-grub-1.c733e703.png",i="/assets/kernel-grub-2.9004b8f9.png",c="/assets/kernel-refind-0.0ce3ab2b.png",d="/assets/kernel-grub-3.71ad2157.png",b="/assets/kernel-refind-1.baad0d14.png",u="/assets/kernel-version-1.8367daa8.png",C="/assets/kernel-version-2.11e07b36.png",y="/assets/hibernate-1.fdc16784.png",m="/assets/hibernate-same-1.f0fcf526.png",h="/assets/hibernate-same-2.c3634cf0.png",D="/assets/hibernate-same-3.7347fe58.png",g="/assets/try-1.c7bff3d4.png",A="/assets/try-2.1f0d9448.png",v="/assets/try-3.3e79d093.png",f="/assets/refind-1.b6900f3c.png",F="/assets/refind-2.c3c142a9.png",E="/assets/refind-3.716a7d44.png",_="/assets/refind-4.a3a17079.png",k="/assets/refind-5.eaae6d11.png",x="/assets/refind-6.e66402f0.png",B="/assets/refind-7.0489162c.png",w="/assets/refind-8.f8aac68d.png",U="/assets/refind-9.4834ca4d.png",q="/assets/refind-10.eb625ebe.png",I="/assets/refind-11.531c4292.png",S="/assets/refind-12.2c25f631.png",W=JSON.parse('{"title":"可选配置（进阶篇）","description":"","frontmatter":{},"headers":[{"level":2,"title":"🐧 更换可选内核","slug":"🐧-更换可选内核","link":"#🐧-更换可选内核","children":[]},{"level":2,"title":"💤 休眠（hibernate）设置","slug":"💤-休眠-hibernate-设置","link":"#💤-休眠-hibernate-设置","children":[{"level":3,"title":"休眠到 Swap 分区","slug":"休眠到-swap-分区","link":"#休眠到-swap-分区","children":[]},{"level":3,"title":"休眠到 Swap 文件（Btrfs）","slug":"休眠到-swap-文件-btrfs","link":"#休眠到-swap-文件-btrfs","children":[]},{"level":3,"title":"休眠到 Swap 文件（ext4）","slug":"休眠到-swap-文件-ext4","link":"#休眠到-swap-文件-ext4","children":[]},{"level":3,"title":"共同后续步骤","slug":"共同后续步骤","link":"#共同后续步骤","children":[]}]},{"level":2,"title":"🔍 rEFind","slug":"🔍-refind","link":"#🔍-refind","children":[]}],"relativePath":"guide/advanced/optional-cfg-2.md","lastUpdated":1680324965000}'),L={name:"guide/advanced/optional-cfg-2.md"},P=e(`<h1 id="可选配置-进阶篇" tabindex="-1">可选配置（进阶篇） <a class="header-anchor" href="#可选配置-进阶篇" aria-hidden="true">#</a></h1><blockquote><h3 id="🎑-一分耕耘-一分收获" tabindex="-1">🎑 一分耕耘，一分收获 <a class="header-anchor" href="#🎑-一分耕耘-一分收获" aria-hidden="true">#</a></h3><p>这一节的配置都是可选的。这些配置根据个人喜好进行配置，往往可以让系统变得更好用</p></blockquote><h2 id="🐧-更换可选内核" tabindex="-1">🐧 更换可选内核 <a class="header-anchor" href="#🐧-更换可选内核" aria-hidden="true">#</a></h2><p>如有需要可参阅 <a href="https://wiki.archlinux.org/title/Kernel_(%E7%AE%80%E4%BD%93%E4%B8%AD%E6%96%87)" target="_blank" rel="noreferrer">ArchWiki 相关内容</a> 以及 <a href="http://www.rodsbooks.com/refind/linux.html" target="_blank" rel="noreferrer">rEFind 官方文档</a>。</p><p>除了 <a href="https://archlinux.org/packages/core/x86_64/linux/" target="_blank" rel="noreferrer">linux</a> 内核，还有以下官方支持的内核可供选择：</p><ul><li><a href="https://archlinux.org/packages/extra/x86_64/linux-zen/" target="_blank" rel="noreferrer">linux-zen</a><ul><li>一些内核黑客合作的结果，是适合日常使用的优秀内核</li><li>以吞吐量和功耗为代价来换取性能</li><li>相对 linux 内核加入了 Fsync 功能，在一些采用 .Net 的 wine 游戏中会有 <a href="https://github.com/ValveSoftware/Proton/issues/3706#issuecomment-636632984" target="_blank" rel="noreferrer">明显的性能提升</a></li></ul></li><li><a href="https://archlinux.org/packages/core/x86_64/linux-lts/" target="_blank" rel="noreferrer">linux-lts</a><ul><li>包含了最新的长期支持的 Linux 内核和内核模块</li><li>可以放心地在更长的时间内享受相同的内核版本</li><li>若硬件不是最新硬件，则可以通过安装稍早的 LTS 内核提高稳定性</li></ul></li><li><a href="https://archlinux.org/packages/extra/x86_64/linux-hardened/" target="_blank" rel="noreferrer">linux-hardened</a><ul><li>更加注重安全的内核，采用一系列 <a href="https://github.com/GrapheneOS/linux-hardened" target="_blank" rel="noreferrer">加固补丁</a> 以减少内核和用户空间产生漏洞的风险</li><li>启用了一些加固选项，比如用户命名空间（同时通过补丁禁用未授权用户的访问）、审计以及 SELinux</li><li>需要注意的是使用此内核时部分软件包将不起作用</li></ul></li></ul><div class="vp-code-group"><div class="tabs"><input type="radio" name="group-muImu" id="tab-c9Fboay" checked="checked"><label for="tab-c9Fboay">zen</label><input type="radio" name="group-muImu" id="tab-87X3nrl"><label for="tab-87X3nrl">lts</label><input type="radio" name="group-muImu" id="tab-xQgVEzA"><label for="tab-xQgVEzA">hardened</label></div><div class="blocks"><div class="language-bash active line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">pacman</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-S</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">linux-zen</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">linux-zen-headers</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">pacman</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-S</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">linux-lts</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">linux-lts-headers</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">pacman</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-S</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">linux-hardened</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">linux-hardened-headers</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></div></div><p><img src="`+l+'" alt="zen"></p><p><img src="'+p+'" alt="lts"></p><p><img src="'+o+`" alt="hardened"></p><div class="tip custom-block"><p class="custom-block-title">ℹ️ 提示</p><p>更换内核后还需要留意部分应用需要更换为相应的 dkms 版本。</p><p>本指南涉及的应用如下：</p><ul><li><a href="/guide/rookie/graphic-driver.html#nvidia-独立显卡"><code>nvidia-dkms</code></a></li><li><a href="/guide/rookie/graphic-driver.html#电源管理"><code>bbswitch-dkms</code></a></li></ul></div><blockquote><h4 id="📑-相关资料-dkms" tabindex="-1">📑 相关资料：DKMS <a class="header-anchor" href="#📑-相关资料-dkms" aria-hidden="true">#</a></h4><p>DKMS，即 <strong>D</strong>ynamic <strong>K</strong>ernel <strong>M</strong>odule <strong>S</strong>ystem。可以使内核变更（如升级）后自动编译模块，适配新内核。</p></blockquote><ol start="2"><li>为了让 GRUB 记住最后在 GRUB 引导菜单里选择的内核，以便在下次启动时自动使用对应的内核，需要编辑 <code>/etc/default/grub</code> 文件：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">vim</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/etc/default/grub</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>在适当位置添加或修改为如下三行：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">GRUB_DEFAULT</span><span style="color:#89DDFF;">=</span><span style="color:#C3E88D;">saved</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># 默认使用保存的内核条目</span></span>
<span class="line"><span style="color:#A6ACCD;">GRUB_SAVEDEFAULT</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">true</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># 保存最后一个使用的内核条目</span></span>
<span class="line"><span style="color:#A6ACCD;">GRUB_DISABLE_SUBMENU</span><span style="color:#89DDFF;">=</span><span style="color:#C3E88D;">y</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># 可选，禁用 GRUB 子菜单，使选择内核的操作更简单</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p><img src="`+t+'" alt="kernel-grub_step-0"></p><p><img src="'+r+`" alt="kernel-grub_step-1"></p><ol start="3"><li>安装完毕后通过以下命令更新一下引导配置即可：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">grub-mkconfig</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/boot/grub/grub.cfg</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="`+i+`" alt="kernel-grub_step-2"></p><ol start="4"><li>rEFind 不需要做额外步骤，但会使用安装的最后一个内核作为默认内核。若想改变默认启动内核，需要修改 rEFind 配置文件：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">vim</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/boot/efi/EFI/refind/refind.conf</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><div class="tip custom-block"><p class="custom-block-title">ℹ️ 提示</p><p>若您不是按照本指南章节 <a href="/guide/rookie/basic-install.html">archlinux 基础安装</a> 安装的 archlinux，请确认您的 EFI 分区（ESP）位置。</p></div><p>将 <code>fold_linux_kernels false</code> 前面的注释（<code>#</code>）去掉：</p><p><img src="`+c+'" alt="kernel-refind-0"></p><p>诚然，这不是一个优雅（elegant）的方案。它会将所有的内核直接在引导菜单首页显示出来，而不是在子菜单选择。但 <a href="https://bbs.archlinux.org/viewtopic.php?id=258751" target="_blank" rel="noreferrer">唯有这样</a> rEFind 才能记住最后选择的内核，而不是安装的最后一个内核。</p><ol start="5"><li>重启，查看引导菜单效果：</li></ol><p><img src="'+d+'" alt="kernel-grub_step-3"></p><p><img src="'+b+`" alt="kernel-refind-1"></p><ol start="6"><li>使用以下命令查看内核：</li></ol><div class="vp-code-group"><div class="tabs"><input type="radio" name="group-ktwRW" id="tab-WgABf3S" checked="checked"><label for="tab-WgABf3S">uname</label><input type="radio" name="group-ktwRW" id="tab-jA2ChC0"><label for="tab-jA2ChC0">neofetch</label></div><div class="blocks"><div class="language-bash active line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">uname</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-r</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">neofetch</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></div></div><p><img src="`+u+'" alt="kernel-version-1"></p><p><img src="'+C+`" alt="kernel-version-2"></p><h2 id="💤-休眠-hibernate-设置" tabindex="-1">💤 休眠（hibernate）设置 <a class="header-anchor" href="#💤-休眠-hibernate-设置" aria-hidden="true">#</a></h2><p>如有需要可以参阅 <a href="https://wiki.archlinux.org/title/Power_management_(%E7%AE%80%E4%BD%93%E4%B8%AD%E6%96%87)/Suspend_and_hibernate_(%E7%AE%80%E4%BD%93%E4%B8%AD%E6%96%87)" target="_blank" rel="noreferrer">ArchWiki 相关内容</a>。</p><p>KDE 自身提供开箱即用的睡眠功能（sleep），即将系统挂起到内存，消耗少量的电量。休眠（hibernate）则会将系统挂起到交换分区或交换文件，几乎不消耗电量。</p><p>挂起到硬盘的映像大小一般最大为物理内存的 <code>2/5</code>，其值在 <code>/sys/power/image_size</code> 中确定。故若想使用休眠功能，Swap 大小一般设置为物理内存的 60% 即可。</p><div class="tip custom-block"><p class="custom-block-title">ℹ️ 提示</p><p>若 Swap 分区大小不够，则可尝试创建并休眠到 Swap 文件中。</p><p>但是需要注意！休眠镜像不能跨多个交换分区和 / 或交换文件。它必须完全适配<strong>一个交换分区或一个交换文件</strong>。</p></div><h3 id="休眠到-swap-分区" tabindex="-1">休眠到 Swap 分区 <a class="header-anchor" href="#休眠到-swap-分区" aria-hidden="true">#</a></h3><ol><li>通过以下命令确认 Swap 分区的 <code>UUID</code>：</li></ol><div class="vp-code-group"><div class="tabs"><input type="radio" name="group-maram" id="tab-Oh3yn4C" checked="checked"><label for="tab-Oh3yn4C">lsblk</label><input type="radio" name="group-maram" id="tab-IxePpCb"><label for="tab-IxePpCb">blkid</label></div><div class="blocks"><div class="language-bash active line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">lsblk</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">name,mountpoint,size,uuid</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">blkid</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></div></div><p>输出结果应类似：</p><div class="vp-code-group"><div class="tabs"><input type="radio" name="group-MJ3z0" id="tab-oQZYp2f" checked="checked"><label for="tab-oQZYp2f">lsblk</label><input type="radio" name="group-MJ3z0" id="tab-iLR5cXA"><label for="tab-iLR5cXA">blkid</label></div><div class="blocks"><div class="language-bash active line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight has-highlighted-lines" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">NAME</span><span style="color:#A6ACCD;">   </span><span style="color:#C3E88D;">MOUNTPOINT</span><span style="color:#A6ACCD;">              </span><span style="color:#C3E88D;">SIZE</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">UUID</span></span>
<span class="line"><span style="color:#FFCB6B;">sda</span><span style="color:#A6ACCD;">                             </span><span style="color:#C3E88D;">64G</span></span>
<span class="line"><span style="color:#FFCB6B;">├─sda1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/boot/efi</span><span style="color:#A6ACCD;">               </span><span style="color:#C3E88D;">244M</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">E519-88D8</span></span>
<span class="line"><span style="color:#FFCB6B;">├─sda2</span><span style="color:#A6ACCD;">                           </span><span style="color:#C3E88D;">1G</span></span>
<span class="line"><span style="color:#FFCB6B;">├─sda3</span><span style="color:#A6ACCD;">                           </span><span style="color:#C3E88D;">1G</span></span>
<span class="line highlighted"><span style="color:#FFCB6B;">├─sda4</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">SWAP</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;">                    </span><span style="color:#C3E88D;">4G</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">13ec7b86-eb9c-45a9-ae50-9606279b506a</span></span>
<span class="line"><span style="color:#FFCB6B;">└─sda5</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/run/timeshift/backup</span><span style="color:#A6ACCD;">  </span><span style="color:#C3E88D;">57.8G</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">860b9d91-590c-44d0-9d38-e4bf137b5d90</span></span>
<span class="line"><span style="color:#FFCB6B;">sr0</span><span style="color:#A6ACCD;">                          </span><span style="color:#C3E88D;">755.3M</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">2021</span><span style="color:#C3E88D;">-</span><span style="color:#F78C6C;">05</span><span style="color:#C3E88D;">-</span><span style="color:#F78C6C;">01</span><span style="color:#C3E88D;">-</span><span style="color:#F78C6C;">05</span><span style="color:#C3E88D;">-</span><span style="color:#F78C6C;">18</span><span style="color:#C3E88D;">-</span><span style="color:#F78C6C;">20</span><span style="color:#C3E88D;">-</span><span style="color:#F78C6C;">00</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight has-highlighted-lines" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">/dev/sr0:</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">BLOCK_SIZE=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">2048</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">UUID=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">2021-05-01-05-18-20-00</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">LABEL=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">ARCH_202105</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">TYPE=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">iso9660</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">PTTYPE=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">PMBR</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line highlighted"><span style="color:#FFCB6B;">/dev/sda4:</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">UUID=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">13ec7b86-eb9c-45a9-ae50-9606279b506a</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">TYPE=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">swap</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">PARTUUID=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">7a9e75d7-eca2-c849-9372-85c6889a7861</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#FFCB6B;">/dev/sda2:</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">PARTUUID=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">bfc5f742-be1e-cb41-911f-ec5466d670de</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#FFCB6B;">/dev/sda5:</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">LABEL=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">myArch</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">UUID=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">860b9d91-590c-44d0-9d38-e4bf137b5d90</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">UUID_SUB=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">5551b2c6-b767-4459-b7fc-de91500baec0</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">BLOCK_SIZE=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">4096</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">TYPE=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">btrfs</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">PARTUUID=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">815c3576-3bb8-dc4e-86d4-f41c45cffd42</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#FFCB6B;">/dev/sda3:</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">PARTUUID=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">23bddb1f-4ef9-844c-a92a-805a4063e3c1</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#FFCB6B;">/dev/sda1:</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">SEC_TYPE=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">msdos</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">UUID=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">E519-88D8</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">BLOCK_SIZE=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">512</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">TYPE=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">vfat</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">PARTUUID=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">1976beb2-c881-8046-9066-21d5dac457ce</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div></div></div><blockquote><h4 id="📑-相关资料-uuid" tabindex="-1">📑 相关资料：UUID <a class="header-anchor" href="#📑-相关资料-uuid" aria-hidden="true">#</a></h4><p>UUID 即 <strong>U</strong>niversally <strong>U</strong>nique <strong>Id</strong>entifier（通用唯一识别码），它可以帮助 Linux 系统识别一个磁盘分区而不是块设备文件。</p></blockquote><ol start="2"><li>使用 <code>vim</code> 编辑 <code>/etc/default/grub</code> 文件：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">vim</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/etc/default/grub</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>将相关参数加入内核启动参数中 —— 找到 <code>GRUB_CMDLINE_LINUX_DEFAULT</code> 一行，在其值后添加类似如下数据（根据你自身的 UUID 确定，参数之间以空格分隔）：</p><div class="language-conf line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">conf</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">resume=UUID=13ec7b86-eb9c-45a9-ae50-9606279b506a</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p><img src="`+y+`" alt="hibernate_step-1"></p><blockquote><h4 id="🍧-碎碎念" tabindex="-1">🍧 碎碎念 <a class="header-anchor" href="#🍧-碎碎念" aria-hidden="true">#</a></h4><p>这个内核启动参数在步骤 <a href="/guide/rookie/basic-install.html#_17-安装引导程序">17. 安装引导程序</a> 中也修改过，现在应该稍微熟悉一点了吧？</p></blockquote><ol start="3"><li>接下来参考步骤 <a href="./optional-cfg-2.html#共同后续步骤">共同后续步骤</a> 完成配置。</li></ol><h3 id="休眠到-swap-文件-btrfs" tabindex="-1">休眠到 Swap 文件（Btrfs） <a class="header-anchor" href="#休眠到-swap-文件-btrfs" aria-hidden="true">#</a></h3><h3 id="休眠到-swap-文件-ext4" tabindex="-1">休眠到 Swap 文件（ext4） <a class="header-anchor" href="#休眠到-swap-文件-ext4" aria-hidden="true">#</a></h3><ol><li>首先确认 Swap 文件所在分区的 <code>UUID</code>：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">findmnt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-no</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">UUID</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-T</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/swapfile</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ol start="2"><li>确认 Swap 文件的偏移值：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">filefrag</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-v</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/swapfile</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>第一行数据中的 <code>physical_offset</code> 一列的值即为所需要的数据。</p><ol start="3"><li>随后将这两个参数加入内核启动参数中。通过 <code>vim</code> 编辑 <code>/etc/default/grub</code> 文件：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">vim</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/etc/default/grub</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>找到 <code>GRUB_CMDLINE_LINUX_DEFAULT</code> 一行，在其值后添加类似如下两项数据，内容根据刚才查询的 <code>UUID</code> 以及偏移值确定。参数以空格分隔：</p><div class="language-conf line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">conf</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">resume=UUID=9a940a0a-fa72-4973-9ccc-3eb93ad73b37 resume_offset=6418432</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><ol start="4"><li>接下来参考步骤 <a href="./optional-cfg-2.html#共同后续步骤">共同后续步骤</a> 完成配置。</li></ol><h3 id="共同后续步骤" tabindex="-1">共同后续步骤 <a class="header-anchor" href="#共同后续步骤" aria-hidden="true">#</a></h3><ol><li>通过以下命令更新 GRUB 配置：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">grub-mkconfig</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/boot/grub/grub.cfg</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="`+m+`" alt="hibernate-same_step-1"></p><ol start="2"><li>除此之外，还需配置 <code>initranfs</code> 的 <code>resume</code> 钩子。使用 <code>vim</code> 编辑 <code>/etc/mkinitcpio.conf</code>：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">vim</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/etc/mkinitcpio.conf</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>在 HOOKS 行添加 <code>resume</code> 值。注意，<code>resume</code> 需要加入在 <code>udev</code> 后。若使用了 LVM 分区，<code>resume</code> 需要加入在 <code>lvm2</code> 后：</p><p><img src="`+h+`" alt="hibernate-same_step-2"></p><div class="tip custom-block"><p class="custom-block-title">ℹ️ 提示</p><p>使用 Intel CPU 并且为触摸板加载 <code>intel_lpss_pci</code> 模块的笔记本电脑，可能会在唤醒时发生内核崩溃（Caps Lock 灯闪烁），黑屏并无法成功唤醒。</p><p>此时需要编辑 <code>/etc/mkinitcpio.conf</code>，在 MODULES 行添加 <code>intel_lpss_pci</code> 值：</p><div class="language-conf line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">conf</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">MODULES=(intel_lpss_pci)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div></div><ol start="3"><li>最后通过以下命令重新生成 <code>initramfs</code> 镜像：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">mkinitcpio</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-P</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="`+D+'" alt="hibernate-same_step-3"></p><ol start="4"><li><p>重启电脑</p></li><li><p>重启之后可以开几个应用，尝试休眠：</p></li></ol><p><img src="'+g+'" alt="try-1"></p><p><img src="'+A+'" alt="try-2"></p><p><img src="'+v+`" alt="try-3"></p><p>虚拟机也可以通过休眠成功唤醒。</p><div class="tip custom-block"><p class="custom-block-title">ℹ️ 提示</p><p>虚拟机可能在休眠唤醒后的关机时发生内核崩溃（Caps Lock 灯也会闪烁）。</p></div><h2 id="🔍-refind" tabindex="-1">🔍 rEFind <a class="header-anchor" href="#🔍-refind" aria-hidden="true">#</a></h2><p>如有需要可以参考 <a href="https://wiki.archlinux.org/title/REFInd_(%E7%AE%80%E4%BD%93%E4%B8%AD%E6%96%87)" target="_blank" rel="noreferrer">ArchWiki 相关内容</a>。</p><p>相对于 GRUB，rEFind 的启动项更加灵活，相对来说也更加美观。</p><ol><li>使用以下命令安装 <code>refind</code> 包：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">pacman</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-S</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">refind</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="`+f+`" alt="refind-1"></p><ol start="2"><li>使用以下命令安装 rEFInd 启动管理器到 EFI 分区：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">refind-install</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="`+F+`" alt="refind-2"></p><ol start="3"><li>使用以下命令复查安装情况：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">ls</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-ahl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/boot/efi/EFI/</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><div class="tip custom-block"><p class="custom-block-title">ℹ️ 提示</p><p>若您不是按照本指南章节 <a href="./basic-install.html">archlinux 基础安装</a> 安装的 archlinux，请确认您的 EFI 分区（ESP）位置。</p></div><p><img src="`+E+`" alt="refind-3"></p><p>输出结果应该可以看到 <code>refind</code> 文件夹。</p><ol start="4"><li>使用 <code>vim</code> 编辑 <code>/boot/efi/EFI/refind/refind.conf</code> 文件：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">vim</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/boot/efi/EFI/refind/refind.conf</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>修改等待时间为合适数值（自定义）：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">timeout</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">5</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="`+_+`" alt="refind-4"></p><p>为了使 rEFInd 支持 archlinux 内核命名方案并使其与各自的 initramfs 镜像相匹配，取消注释并编辑 <code>extra_kernel_version_strings</code> 所在行：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">extra_kernel_version_strings</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">linux-hardened,linux-zen,linux-lts,linux</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="`+k+`" alt="refind-5"></p><p>为了允许在 Btrfs 子卷上进行内核自动检测，取消注释并编辑 <code>also_scan_dirs</code>，在最后加上 <code>@/boot</code>：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">also_scan_dirs</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">boot,ESP2:EFI/linux/kernels,@/boot</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><div class="tip custom-block"><p class="custom-block-title">ℹ️ 提示</p><p>若您不是按照本指南章节 <a href="./basic-install.html">archlinux 基础安装</a> 安装的 archlinux：</p><ul><li>若使用了 ext4 文件系统，则不用修改这一行。</li><li>若使用了 Btrfs 文件系统，请确认根目录或 <code>/boot</code> 目录对应子卷位置（简而言之这个位置是从 Btrfs 文件系统最上层开始寻找的）</li></ul></div><p><img src="`+x+`" alt="refind-6"></p><ol start="5"><li><p>保存并退出 <code>vim</code></p></li><li><p>若安装 archlinux 时按照步骤 <a href="/guide/rookie/basic-install.html#_7-分区和格式化-使用-btrfs-文件系统">7. 分区和格式化（使用 Btrfs 文件系统）</a> 使用了 Btrfs 文件系统，为了让 rEFind 支持 Btrfs 子卷，需要使用以下命令手动为 rEFind 安装驱动：</p></li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">cp</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/usr/share/refind/drivers_x64/btrfs_x64.efi</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/boot/efi/EFI/refind/drivers_x64/btrfs_x64.efi</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="`+B+`" alt="refind-7"></p><ol start="7"><li>使用以下命令复查安装情况：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">ls</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-ahl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/boot/efi/EFI/refind/drivers_x64/</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>输出如下：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">-rwxr-xr-x</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">root</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">root</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">114K</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">Jun</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">15</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">23</span><span style="color:#C3E88D;">:</span><span style="color:#F78C6C;">00</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/boot/efi/EFI/refind/drivers_x64/btrfs_x64.efi</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ol start="8"><li>使用以下命令在内核所在文件夹（<code>/boot</code>）创建包含内核参数的 <code>refind_linux.conf</code> 文件：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">mkrlconf</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--force</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><div class="tip custom-block"><p class="custom-block-title">ℹ️ 提示</p><p>对于自动检测到的内核，可以在 <code>/boot/refind_linux.conf</code> 中显式指定内核参数，也可以依靠 rEFInd 识别根分区和内核参数的能力。此处便是使用了后者。</p><p>若要修改内核参数，可以用 GRUB 使用新内核参数登录系统后重新使用此步骤命令重新生成 <code>/boot/refind_linux.conf</code> 文件，也可以直接在 <code>/boot/refind_linux.conf</code> 显式指定内核参数。</p></div><ol start="9"><li>加载微码的内核参数不会自动创建。为了加载微码，需要手动编辑 <code>/boot/refind_linux.conf</code>：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">vim</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/boot/refind_linux.conf</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>在内核参数中加入以下内容：</p><div class="language-conf line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">conf</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">initrd=@\\boot\\intel-ucode.img initrd=@\\boot\\initramfs-%v.img # Intel</span></span>
<span class="line"><span style="color:#A6ACCD;">initrd=@\\boot\\amd-ucode.img initrd=@\\boot\\initramfs-%v.img # AMD</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><div class="tip custom-block"><p class="custom-block-title">ℹ️ 提示</p><p>若您不是按照本指南章节 <a href="/guide/rookie/basic-install.html">archlinux 基础安装</a> 安装的 archlinux，请确认您的微码位置。</p><p>此外，参数 <code>initrd</code> 中使用反斜杠（<code>\\</code>）而不是斜杠（<code>/</code>）作为路径分隔符。</p></div><ol start="10"><li>复查 <code>/boot/refind_linux.conf</code> 文件：</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">cat</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/boot/refind_linux.conf</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>输出如下：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">Boot with standard options</span><span style="color:#89DDFF;">&quot;  &quot;</span><span style="color:#C3E88D;">root=UUID=860b9d91-590c-44d0-9d38-e4bf137b5d90 rw rootflags=subvol=@ loglevel=5 nowatchdog resume=UUID=13ec7b86-eb9c-45a9-ae50-9606279b506a initrd=@\\boot\\intel-ucode.img initrd=@\\boot\\initramfs-%v.img</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">Boot to single-user mode</span><span style="color:#89DDFF;">&quot;    &quot;</span><span style="color:#C3E88D;">root=UUID=860b9d91-590c-44d0-9d38-e4bf137b5d90 rw rootflags=subvol=@ loglevel=5 nowatchdog resume=UUID=13ec7b86-eb9c-45a9-ae50-9606279b506a initrd=@\\boot\\intel-ucode.img initrd=@\\boot\\initramfs-%v.img single</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">Boot with minimal options</span><span style="color:#89DDFF;">&quot;   &quot;</span><span style="color:#C3E88D;">ro root=UUID=860b9d91-590c-44d0-9d38-e4bf137b5d90</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><div class="tip custom-block"><p class="custom-block-title">ℹ️ 提示</p><p>若需要配置默认引导内核，请参阅步骤 <a href="./optional-cfg-2.html#🐧-更换可选内核">🐧 更换可选内核</a>。</p></div><ol start="11"><li>重启电脑，便可以看到 rEFind 的引导页面了：</li></ol><p><img src="`+w+'" alt="refind-8"></p><p>这个选项是通过 GRUB 套娃启动系统。</p><p><img src="'+U+'" alt="refind-9"></p><p>这个选项是通过 rEFind 直接引导进入系统。</p><div class="tip custom-block"><p class="custom-block-title">ℹ️ 提示</p><p>在对应系统的位置按下 <code>F2</code> 可进入子菜单：</p></div><div class="tip custom-block"><p class="custom-block-title">ℹ️ 提示</p><p>若引导了 win10 也会显示出来。</p></div><div class="tip custom-block"><p class="custom-block-title">ℹ️ 提示</p><p>后面两位是虚拟机安装 archlinux 后留下来的安装镜像（实体机没有），不想要可以请使用以下步骤移除</p><ol><li>关闭虚拟机后打开虚拟机设置 &gt; 侧栏 <code>存储</code> &gt; 点击右侧 <code>分配光驱</code> 的右侧光盘小图标：</li></ol><p><img src="'+q+'" alt="refind-10"></p><ol start="2"><li>点击 <code>移除虚拟盘</code>：</li></ol><p><img src="'+I+'" alt="refind-11"></p><ol start="3"><li>开启虚拟机，查看效果：</li></ol><p><img src="'+S+'" alt="refind-12"></p></div><p>关于 rEFind 的美化请参阅 <a href="./.html">系统美化</a>。</p>',137),T=[P];function R(G,M,z,O,N,K){return n(),a("div",null,T)}const Y=s(L,[["render",R]]);export{W as __pageData,Y as default};
